import React, { useContext } from "react";
import { JobContext } from "./JobsContext";

function Home(props) {
  const [state, setState] = useContext(JobContext);

  const text = state.jobs.map((job) => <li key={job.id}>{job.title}</li>);
  return (
    <div>
      <ul>{!state.isLoading ? text : <div>Loading...</div>}</ul>
      <h1>{localStorage.getItem('loggedUser')}</h1>
    </div>
  );
}

export default Home;
