import React, { useContext, useEffect, useState } from "react";
import { NewsContext } from "./NewsContext";
import Loading from "./Loading";

const NewsPage = () => {
  //   const [loadedNews, setLoadedNews] = useContext(NewsContext);
  //   const [loadedNews, setLoadedNews] = news;

  const [loadedNews, setLoadedNews] = useState([]);

  useEffect(() => {
    fetch(
      "http://newsapi.org/v2/everything?q=bitcoin&from=2020-10-18&sortBy=publishedAt&apiKey=f46893f70f12415eb802523a642a40a5"
    )
      .then((res) => res.json())
      .then((data) => setLoadedNews(data));
  });

  return (
    <React.Fragment>
      {loadedNews.map((el) => (
        <p>{el}</p>
      ))}
    </React.Fragment>
  );
};

export default NewsPage;
