import React, { useContext } from "react";
import { JobContext } from "./JobsContext";
import Loading from "./Loading";
import { Link } from "react-router-dom";

export default function SearchBar() {
  const { jobs, loading } = useContext(JobContext);
  const [isLoading] = loading;

  const [loadedJobs, setLoadedJobs] = jobs;
  const [searchTerm, setSearchTerm] = React.useState("");
  const [searchResults, setSearchResults] = React.useState([]);

  const handleChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const displayJobs = (array) => {
    return array.map((job) => (
      <Link to={"/job/" + job.id}>
        <div className="card" key={job.id}>
          <h3>{job.company}</h3>
          <img src={job.company_logo} alt="" />
          <p>Job title: {job.title}</p>
          <p>Location: {job.location}</p>
          <p>Job type: {job.type}</p>
        </div>
      </Link>
    ));
  };

  React.useEffect(() => {
    const results = loadedJobs.filter((job) =>
      job.title.toString().toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  }, [searchTerm, loadedJobs]);

  const search = (
    <div>
      <form className="form-inline my-2 my-lg-0">
        <label>
          <input
            class="form-control mr-sm-2"
            type="search"
            onChange={handleChange}
            value={searchTerm}
            type="text"
            placeholder="Search jobs"
            name="search-job"
          ></input>
        </label>
      </form>
    </div>
  );

  const content = isLoading ? (
    <Loading />
  ) : (
    <div className="cards">
      {searchResults.length > 0
        ? displayJobs(searchResults)
        : displayJobs(loadedJobs)}
    </div>
  );

  return (
    <React.Fragment>
      {search}
      {content}
    </React.Fragment>
  );
}
