import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const JobContext = createContext();

export const JobProvider = (props) => {
  const [state, setState] = useState({ jobs: [], isLoading: false});

  useEffect(() => {
    setState({})
    axios
      .get(
        `https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?page=3`
      )
      .then((res) => {
        setState({jobs: res.data, isLoading: false});
      });
  }, []);

  return (
    <JobContext.Provider value={[state, setState]}>
      {props.children}
    </JobContext.Provider>
  );
};
