import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const CompaniesContext = createContext();

export const CompanyProvider = (props) => {
  const [loadedCompanies, setLoadedCompanies] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    axios.get(`http://localhost:8080/api/v1/companies`).then((res) => {
      setIsLoading(false);
        setLoadedCompanies(res.data);
        console.log(loaded);
    });
  }, []);

  return (
    <CompaniesContext.Provider
      value={{
        companies: [loadedCompanies, setLoadedCompanies],
        page: [pageNumber, setPageNumber],
        loading: [isLoading, setIsLoading],
      }}
    >
      {props.children}
    </CompaniesContext.Provider>
  );
};
